<style>
    .example-wrapper { margin: 1em auto; max-width: 800px; width: 95%; font: 18px/1.5 sans-serif; }
    .example-wrapper code { background: #F5F5F5; padding: 2px 6px; }

    .card-signin {
  border: 0;
  border-radius: 1rem;
  box-shadow: 0 0.5rem 1rem 0 rgba(0, 0, 0, 0.1);
}

.card-signin .card-title {
  margin-bottom: 2rem;
  font-weight: 300;
  font-size: 1.5rem;
}

.card-signin .card-body {
  padding: 2rem;
}

.form-signin {
  width: 100%;
}

.form-signin .btn {
  font-size: 80%;
  border-radius: 5rem;
  letter-spacing: .1rem;
  font-weight: bold;
  padding: 1rem;
  transition: all 0.2s;
}

.form-label-group {
  position: relative;
  margin-bottom: 1rem;
}

.form-label-group>input,
.form-label-group>label {
  padding: var(--input-padding-y) var(--input-padding-x);
}
.btn-google {
  color: white;
  background-color: #ea4335;
}

.btn-facebook {
  color: white;
  background-color: #3b5998;
}

</style>


<div class=\"row\">
      <div class=\"col-sm-9 col-md-7 col-lg-5 mx-auto\">
        <div class=\"card card-signin my-5\">
          <div class=\"card-body\">
            <h5 class=\"card-title text-center\">Fokotany Login</h5>
            <form class=\"form-signin\" action=\"{{ path(\"login\") }}\" method=\"POST\" >

            {% if error %} 
                <div class=\"alert alert-danger\">
                    <p>{{ error.messageKey }}</p>
                </div>
            {% endif %}

              <div class=\"form-group\">
                <input type=\"text\" name=\"_username\"  class=\"form-control\" value=\"{{lastUsername}}\" >
              </div>

              <div class=\"form-group\">
                <input type=\"password\"  class=\"form-control\" placeholder=\"Password\" required >
              </div>

              <div class=\"custom-control custom-checkbox mb-3\">
                <input type=\"checkbox\" class=\"custom-control-input\" id=\"customCheck1\">
                <label class=\"custom-control-label\" for=\"customCheck1\">Remember password</label>
              </div>
              <button class=\"btn btn-block btn-primary \" type=\"submit\">Connect</button>
              <hr class=\"my-4\">
              <button class=\"btn  btn-google btn-block text-uppercase\" type=\"submit\"><i class=\"fa fa-google mr-2\"></i> connexion avec Google</button>
              <button class=\"btn  btn-facebook btn-block text-uppercase\" type=\"submit\"><i class=\"fa fa-facebook-f mr-2\"></i> connexion avec Facebook</button>
            </form>
          </div>
        </div>
      </div>
    </div>

{% endblock %}
